import React, { Component } from 'react'
import axios from 'axios'
import PromoCard from '../PromoCard/PromoCard';
import './Container.css'
import '../responce.css'
import yandex from '../../logocompany/yandex.jpg'
import deli from '../../logocompany/deli.jpg'
