import React, { Component } from 'react'
import './PromoCard.css'
import { CopyToClipboard } from 'react-copy-to-clipboard';
import deli from './'

export default class PromoCard extends Component {

  state = {
    copied: false
  }

  render() {
    return (
      <div className="card">
        <h1>{this.props.company}</h1>
        <img src="./deli.jpg" alt="" />
        <br />
        <h3>{this.props.info}</h3>
        <div className="promo">{this.props.promo}</div>
        <span>Активировали {this.props.number} раз</span>
        <br />

        <CopyToClipboard text={this.props.promo}
          onCopy={() => this.setState({ copied: true })}>
          <button className="btncopy" >Скопировать</button>
        </CopyToClipboard>




      </div>

    )
  }
}
